import mixins from '../../../common/mixin';
import navBar from '../../../component/navBar';
import { Toast, Dialog, Popup } from 'vant';
import countTo from 'vue-count-to';


export default {
    components: {
        navBar,
        Dialog,
        Toast,
        Popup,
        countTo
    },
    mixins: [mixins],
    data: () => ({
        aaaa:0,
        isLoadSuccess: false,			// 页面是否完成加载
        warrant: {},					// 加载回来的数据
        account: '',					// 登录用户手机
        showImagePreview: false ,		// 是否显示图片预览
        show: false,                    //购买弹窗
        code:"",
        order:""  ,                      //订单编号
        judge:false,                    //判断是否购买创世店主
        accede:false,
        isActivw:false,
        codeTime: 60,                   // 手机倒计时
        isCode: false,                  // 是否显示发生验证码
        robbed:true,
        timeES:false,                   //判断是否在时间内
        available:"",                   //可用余额
        handle:true,                    //判断短信验证
        usable:"确认支付",               //点击按钮文字


    }),
    created () {


    },
    // 设置路由钩子，存储页面来源
    beforeRouteEnter(to, from, next) {
        next(vm=>{          //  这里的vm指的就是vue实例，可以用来当做this使用
            sessionStorage.setItem('url_from', from.path);
        })
    },
    mounted () {
        this.handleGetUserInfo();
        this.handleLoad();
    },
    methods: {
        //处理软键盘导致弹框里的按钮响应区域错位
        scrollBack: function () {
            setTimeout(function() {
                var scrollHeight = document.documentElement.scrollTop || document.body.scrollTop || 0;
                window.scrollTo(0, Math.max(scrollHeight - 1, 0));
            }, 100);
        },
        //确认付款
        // 初始化用户信息
        handleGetUserInfo: function() {
            this.request({
                url: 'api/user',
                method: 'GET',
                data: { },
                success: (response) => {
                    this.account=response.mobile

                },
                error: (response) => {

                },
                complete: (response) => {

                }
            });
        },
        //确认付款
        affirmButten:function () {
            //短信验证
            this.request({
                url: 'api/forgetPassword',
                method: 'POST',
                data: {
                    mobile:this.account,
                    code:this.code
                },
                success: (response) => {
                    //提交下单
                    this.request({
                        url: 'api/warrant',
                        method: 'PUT',
                        data: {
                            type:2
                        },
                        success: (response) => {
                            this.show = false;
                            this.judge =true;
                            this.$router.go(0);
                            Toast('创世店主升级成功');

                        },
                        error: (response) => {
                            this.show = false;
                            this.code="";
                        },
                        complete: (response) => {
                        }
                    });
                },
                error: (response) => {

                },
                complete: (response) => {
                }
            });


        },
        //获取验证码链接
        handleGetCode: function () {
               this.request({
                url: 'api/sendMobileCode',
                data: {
                    mobile: this.account
                },
                success: (response) => {
                    this.isCode = !this.isCode;

                    let timer = setInterval(() => {
                        if(this.codeTime > 0) {
                            this.codeTime = this.codeTime - 1;
                        } else {
                            this.codeTime = 60;
                            this.isCode = !this.isCode;

                            clearInterval(timer)
                        }
                    }, 1000)
                },
                error: (response) => {
                },
                complete: () => {

                }
            });
        },
        //激活付款按钮
        handleGetStatus:function () {
            if(this.code.length === 6) {
                this.isActivw = true;

            } else{
                this.isActivw = false;
            }
        },
        //加入社区
        community:function () {
            this.accede =true
        },
        // 创世店主权益
        handleLoad: function() {
            this.request({
                url: 'api/warrant',
                method: 'GET',
                data: {
                    type:2
                },
                success: (response) => {
                    this.warrant = response;
                    let ts = Math.round(new Date().getTime()/1000).toString();
                    if(response.type === 2){
                         this.judge = true
                     }
                    else {
                        if(response.limit.end_time < ts && ts > response.limit.start_time ){
                                 this.timeES = true;
                        }
                    }
                    //判断短信验证码显示隐藏
                    if(Number(response.wallet.astir_currency) < Number(response.amount) ){
                        this.handle = false;
                        this.usable = "可用余额不足"
                    }
                    else {
                        this.handle = true;
                        this.usable = "确认支付"
                    }
                    //可用余额判断
                    if(response.wallet.astir_currency === null){
                        this.available = 0
                    }else {
                        this.available =  response.wallet.astir_currency;
                    }

                },
                error: (response) => {

                },
                complete: (response) => {

                }
            });
        },
        cancel(){


            this.show = false;
            this.code="";

     },
        showPopup(){
            //创世店主立即抢购
            this.request({
                url: 'api/warrant',
                method: 'POST',
                data: {
                    type:2
                },
                success: (response) => {

                    this.order =response;





                },
                error: (response) => {

                },
                complete: (response) => {

                }
            });
            this.show = true;
        },
        //跳转抢购规则
        Buying:function () {
            this.$router.push({
                path: '/buying',
                query: {
                    id:2
                }
            });
        }



    }
}
