import mixins from '../../../../common/mixin';
import navBar from '../../../../component/navBar';
import {Icon, Dialog, Popup,Toast } from 'vant';
import countTo from 'vue-count-to';

export default {
    components: {
        navBar,
        Icon,
        Dialog,
        Popup,
        countTo,
        Toast
    },
    mixins: [mixins],
    data: () => ({
        show: false,		// 是否激活支付按钮
        code: "",
        codeTime: 60,       // 手机倒计时
        isCode: false,     // 是否显示发生验证码
        switcha: true,
        order: {},   //订单信息
        comments: "",  //买家留言
        orderAmount: {},   //订单支付信息
        isActivw: false,     //支付按钮
        orderID: "",         //订单ID
        isDefaultAddress: false,       //判断是否有默认地址
        icon: 3,   //支付方式  1等于ETH 2等于INE 3等于UTD
        unit: "UTD",      //单位
        transferRecord: {},             // 用户地址信息
        support:[],                  //支持的付款方式
        express_price:0             //运费
    }),
    // 设置路由钩子，存储页面来源
    beforeRouteEnter(to, from, next) {
        next(vm => {          //  这里的vm指的就是vue实例，可以用来当做this使用
            sessionStorage.setItem('url_from', from.path);
        })
    },
    created() {

    },
    mounted() {
        this.order = JSON.parse(this.$route.query.order);
        console.log(JSON.parse(this.$route.query.order));
        this.handleGetUserAddress();

    },
    methods: {
      //商品信息
        information:function () {
            this.request({
                url: 'api/store/order',
                method: 'GET',
                data: {
                    id: this.order.id,
                    quantity: this.order.order_total_quantity,
                    sku_id: this.order.sku_id,
                    remark: ''
                },
                success: (response) => {
                    this.express_price = response.express_price
                  console.log(response)
                },
                error: (response) => {
                },
                complete: () => {

                }
            });
        },
        //提交订单
        showPopup: function () {
            this.request({
                url: 'api/store/order',
                method: 'POST',
                data: {
                    id: this.order.id,
                    quantity: this.order.order_total_quantity,
                    sku_id: this.order.sku_id,
                    remark: this.comments,
                },
                success: (response) => {
                    this.orderID = response.id;
                    //获取订单支持币种默认选择第一个
                    this.request({
                        url: 'api/store/goods',
                        method: 'POST',
                        data: {
                            id: this.order.id,
                        },
                        success: (response) => {
                            this.support =response.detail.available_pay_type;
                            if (response.detail.available_pay_type[0] == 1) {
                                this.icon = 1;
                                this.unit = "ETH";
                            }
                            if (response.detail.available_pay_type[0] == 2) {
                                this.icon = 2;
                                this.unit = "INE";
                            }
                            if (response.detail.available_pay_type[0] == 3) {
                                this.icon = 3;
                                this.unit = "UTD";
                            }
                            //支付信息前置获取
                            this.request({
                                url: 'api/store/order',
                                method: 'PUT',
                                data: {
                                    id: this.orderID,
                                    pay_type: response.detail.available_pay_type[0]
                                },
                                success: (response) => {
                                    this.orderAmount = response;
                                    this.show = true;
                                    if (Number(this.orderAmount.balance) >= Number(this.orderAmount.amount)) {
                                        this.isActivw = true;
                                    } else {
                                        this.isActivw = false;
                                    }
                                },
                                error: (response) => {
                                },
                                complete: () => {
                                }
                            });
                        },
                        error: (response) => {
                        },
                        complete: () => {
                        }
                    });

                },
                error: (response) => {
                },
                complete: () => {
                }
            });
        },
        cancel: function () {
            this.$router.replace({
                path: "/plus/shopx/order/orderList",
                query: {}
            });
        },
        //支付方式
        currencyWay: function (index) {

            if(this.support.indexOf(index) === -1){
                Toast('目前不支持此支付方式');
                return
            }
            if (index == 1) {
                this.icon = 1;
                this.unit = "ETH";
            }
            if (index == 2) {
                this.icon = 2;
                this.unit = "INE";
            }
            if (index == 3) {
                this.icon = 3;
                this.unit = "UTD";
            }
            //更改支付方式
            this.request({
                url: 'api/store/order',
                method: 'PUT',
                data: {
                    id: this.orderID,
                    pay_type: index
                },
                success: (response) => {
                    this.orderAmount = response;
                    this.show = true;
                    if (Number(this.orderAmount.balance) >= Number(this.orderAmount.amount)) {
                        this.isActivw = true;
                    } else {
                        this.isActivw = false;
                    }
                },
                error: (response) => {

                },
                complete: () => {

                }
            });
            this.icon = index;
            this.switcha=true;
        },
        //支付
        defray: function () {
            this.request({
                url: 'api/store/order',
                method: 'PATCH',
                data: {
                    id: this.orderID,
                    pay_type: this.icon,

                },
                success: (response) => {
                    this.$router.push({
                        path: '/plus/shopx/order/success',
                        query: {
                            id: this.orderID
                        }
                    })
                },
                error: (response) => {
                },
                complete: () => {

                }
            });
        },
        takeSkip: function () {
            this.$router.push({
                path: "/plus/shopx/address",
                query: {
                    fromOrder: true,
                    order: this.$route.query.order
                }
            })
        },
        // 初始化用户地址信息
        handleGetUserAddress: function() {
            this.information();
            this.request({
                url: 'api/store/address',
                method: 'GET',
                data: { },
                success: (response) => {
                    this.transferRecord = response;
                    this.isDefaultAddress = false;

                    this.transferRecord.list.map((data) => {
                        if(this.transferRecord.default_id == data.id) {
                            this.isDefaultAddress = true;
                            this.order.address = data;
                        }
                    })
                },
                error: (response) => {

                },
                complete: (response) => {
                }
            });
        },
        /*
        获取验证码链接
        handleGetCode: function () {
            this.request({
                url: 'api/sendMobileCode',
                data: {
                    mobile: 15013663961
                },
                success: (response) => {
                    this.isCode = !this.isCode;

                    let timer = setInterval(() => {
                        if(this.codeTime > 0) {
                            this.codeTime = this.codeTime - 1;
                        } else {
                            this.codeTime = 60;
                            this.isCode = !this.isCode;

                            clearInterval(timer)
                        }
                    }, 1000)
                },
                error: (response) => {
                },
                complete: () => {

                }
            });
        },*/
        changingOver: function () {
            this.switcha = false
        },
        changingTwo: function () {
            this.switcha = true
        }
    }
}
