import navBar from '../../../../component/navBar';
import mixins from '../../../../common/mixin';
import { Icon } from 'vant';
export default {
    mixins: [mixins],
    components: {
        navBar,
        Icon
    },
    data: () => ({
        isLoadSuccess: false,
        seachKey: '',
        inviteeList: []
    }),
    created() {
        this.handleInvitee();
    },
    methods: {
        // 邀请人列表
        handleInvitee: function () {
            this.request({
                url: 'api/invitee',
                method: 'GET',
                data: { },
                success: (response) => {
                    this.isLoadSuccess = true;
                    this.inviteeList = response;

                    // 手机号加密
                    var reg=/(\d{3})\d{4}(\d{4})/;
                    this.inviteeList[0].list.map((data) => {
                        data.mobile = data.mobile.replace(reg, "$1****$2")
                    });
                },
                error: (response) => {

                },
                complete: (response) => {

                }
            });
        },
        handleSeach: function () {
            this.request({
                url: 'api/invitee',
                method: 'GET',
                data: {
                    mobile: this.seachKey
                },
                success: (response) => {
                    this.inviteeList = response;

                    // 手机号加密
                    var reg=/(\d{3})\d{4}(\d{4})/;
                    this.inviteeList[0].list.map((data) => {
                        data.mobile = data.mobile.replace(reg, "$1****$2")
                    });
                },
                error: (response) => {

                },
                complete: (response) => {

                }
            });
        }
    }
}
