import { Toast } from 'vant';
import mixins from '../../../common/mixin';
import navBar from '../../../component/navBar';
import countTo from 'vue-count-to';

export default {
    components: {
        Toast,
        navBar,
        countTo
    },
    mixins: [mixins],
    data: () => ({
        integralNumber: null,
        freezeIntegral: null
    }),
    created () {
        this.handleGetUserInfo();
    },
    // 设置路由钩子，存储页面来源
    beforeRouteEnter(to, from, next) {
        next(vm=>{          //  这里的vm指的就是vue实例，可以用来当做this使用
            sessionStorage.setItem('url_from', from.path);
        })
    },
    methods: {
        // 初始化用户信息
        handleGetUserInfo: function() {
            this.request({
                url: 'api/user',
                method: 'GET',
                data: { },
                success: (response) => {
                    this.freezeIntegral = Number(response.binfenji.current_integral);
                },
                error: (response) => {

                },
                complete: (response) => {
                    Toast.clear();
                }
            });
        },
        // 选择全部
        handleAll: function() {
            this.integralNumber = Number(this.freezeIntegral);
        },
        // 提交划转
        handleSublim: function() {
            if(Number(this.integralNumber) <= 0) {
                this.$toast.center('划转数量过小');
                return false;
            }
            this.request({
                url: 'api/transfer',
                method: 'POST',
                data: {
                    nums: Number(this.integralNumber)
                },
                success: (response) => {
                    this.$toast.center('划转成功');

                    this.freezeIntegral -=  Number(this.integralNumber);
                    this.integralNumber = 0;

                    this.handleBack();
                    // this.$router.replace({
                    //     path: '/shopkeeperLoading',
                    //     query: {
                    //         name: '资产划转',
                    //         type: 'integral'
                    //     }
                    // });
                },
                error: (response) => {

                },
                complete: () => {
                    Toast.clear();
                }
            });
        },
        // 划转记录
        handleRecord: function() {
            this.$router.push({
                path: '/myRecord',
                query: {}
            });
        }
    }
}
